    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        ProdutosDTO produto = new ProdutosDTO();
        
        if(produto.verificaDado(cadastroNome,"Nome") && produto.verificaDado(cadastroValor, "Valor")){
            produto.setNome(cadastroNome.getText());
            produto.setValor(Integer.parseInt(cadastroValor.getText()));
            produto.setStatus("A Venda");
            
            ProdutosDAO produtodao = new ProdutosDAO();
            produtodao.cadastrarProduto(produto);
        }
        
        cadastroNome.setText(null);
        cadastroValor.setText(null);
    }//GEN-LAST:event_btnCadastrarActionPerformed

